package com.sandeepbegudem.backend.application.repository;import com.sandeepbegudem.backend.application.entity.Role;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;@Repositorypublic interface RoleRepository extends JpaRepository<Role, Long> {    @Query(value = "select r from Role as r where r.name like %:name%")    Role findByName(@Param("name") String name);}