package com.sandeepbegudem.backend.application.service.impl;import com.sandeepbegudem.backend.application.entity.Role;import com.sandeepbegudem.backend.application.repository.RoleRepository;import com.sandeepbegudem.backend.application.service.RoleService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@Service@Transactionalpublic class RoleServiceImpl implements RoleService {    @Autowired    private RoleRepository roleRepository;    public RoleServiceImpl(RoleRepository roleRepository) {        this.roleRepository = roleRepository;    }    @Override    public Role createRole(String roleName) {        return roleRepository.save(new Role(roleName));    }}