package com.sandeepbegudem.backend.application.service.impl;import com.sandeepbegudem.backend.application.entity.Role;import com.sandeepbegudem.backend.application.entity.User;import com.sandeepbegudem.backend.application.repository.RoleRepository;import com.sandeepbegudem.backend.application.repository.UserRepository;import com.sandeepbegudem.backend.application.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@Service@Transactionalpublic class UserServiceImpl implements UserService {    @Autowired    private UserRepository userRepository;    @Autowired    private RoleRepository roleRepository;    public UserServiceImpl(UserRepository userRepository, RoleRepository roleRepository) {        this.userRepository = userRepository;        this.roleRepository = roleRepository;    }    @Override    public User loadUserByEmail(String email) {        return userRepository.findByEmail(email);    }    @Override    public User createUser(String email, String password) {        return userRepository.save(new User(email, password));    }    @Override    public void assignRoleToUser(String email, String roleName) {        User user = loadUserByEmail(email);        Role role = roleRepository.findByName(roleName);        user.assignRoleToUser(role);    }}